import React, { useEffect, useState } from 'react';
import io from 'socket.io-client';

const port = 5000;
let socket;

const Chat = ({ location }) => {
    const [name, setName] = useState('dwada');
    const [room, setRoom] = useState('');
    const endpoint = `localhost:${port}`;

    useEffect(() => {
        const getName = location.search.substring(6, location.search.indexOf('&'));
        const getRoom = location.search.substring(location.search.indexOf('&') + 6);
        socket = io(endpoint);
        setName(getName);
        setRoom(getRoom);
        socket.emit('join', { getName, getRoom }, ({ error }) => {
            alert(error);
        });
    }, [endpoint, location.search]);
    return (
        <h1>Chat</h1>
    )
}

export default Chat;
